mapper.insert:

	SELECT NVL(MAX(departmentid), 0) + 1
	FROM departments

INSERT INTO departments
	(
		departmentid
       ,departmentname
       ,managerid
       ,locationid
	)
VALUES
	(
		#{departmentname}
       ,#{managerid}
       ,#{locationid}
	)

mapper.update:
UPDATE departments
SET departmentname = #{departments.departmentname}
    ,managerid = #{departments.managerid}
    ,locationid = #{departments.locationid}
WHERE departmentid = #{id}
