mapper.insert:

	SELECT NVL(MAX(department_id), 0) + 1
	FROM departments

INSERT INTO departments
	(
		department_id
       ,department_name
       ,manager_id
       ,location_id
	)
VALUES
	(
		#{departmentname}
       ,#{managerid}
       ,#{locationid}
	)

mapper.update:
UPDATE departments
SET department_name = #{departments.departmentname}
    ,manager_id = #{departments.managerid}
    ,location_id = #{departments.locationid}
WHERE department_id = #{id}
